package week.w197.p5461;

public class Solution {
    public int numSub(String s) {
        int subCount = 0;
        int countOf1 = 0;

        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '0') {
                countOf1 = 0;
                continue;
            }

            countOf1++;
            subCount += countOf1;

            if (subCount > 1000000000) {
                subCount %= 1000000007 ;
            }
        }

        return subCount;
    }


    public static void main(String[] args) {
        Solution so = new Solution();
        String s = "";
        int num = so.numSub(s);
        System.out.println(num);
    }
}
